function parseSegment(buffer, offset) {
    let typeKey = buffer[offset];

    if (typeKey <= 20) {
        if (buffer[offset + 1] == undefined) throw new Error("ParseError: No value for uint8");
        return { typeKey: typeKey, value: buffer[offset + 1], bufferLength: 2 };
