	if (body.indexOf('<!--') > 0) {
		var o = body.indexOf('<!--');
		while (o > 0) {
			var e = body.indexOf('-->', o);
			print("\t" + body.substr(o,e-o+3)) 
			o = body.indexOf('<!--', e);
		}
	}
}

