const fs = require('fs').promises;
const path = require('path');

async function findPackageFor(filepath) {
    let directory = path.resolve(filepath);

    while (directory !== '/') {
        const packagePath = path.join(directory, 'package.json');

        try {
            await fs.access(packagePath); // Check if the file exists
            const packageJson = await fs.readFile(packagePath, 'utf-8');
            const packageData = JSON.parse(packageJson);
            return packageData.name;
        } catch (error) {
            if (error.code === 'ENOENT') {
                directory = path.dirname(directory); // Move up one directory level
            } else {
                throw error; // Re-throw other errors
            }
        }
    }

    throw new Error(`No package.json found for: ${filepath}`);
}

