const path = require('path');

async function findPackageFor(filepath) {
    let directory = path.resolve(filepath);

    while (directory !== '/') {
        const packagePath = path.join(directory, 'package.json');

        try {
            const stats = await fs.stat(packagePath);

            if (stats.isFile()) {
                const packageJson = await fs.readFile(packagePath, 'utf8');
                const packageData = JSON.parse(packageJson);
                return packageData.name;
            }
        } catch (error) {
            // Continue searching if the file does not exist or other errors occur
        }

        directory = path.resolve(directory, '..');
    }

    throw new Error(`No package.json found for ${filepath}`);
}

module.exports.findPackageFor = findPackageFor;
