function invokeWith(msg) {
	// Debugging can be done using println like this
	print('Finding comments in ' + msg.getRequestHeader().getURI().toString()); 

	var body = msg.getResponseBody().toString()
	// Look for html comments
	if (body.indexOf('<!--') > 0) {
		var o = body.indexOf('<!--');
